mapper.update:
update ordertable
set opentime = #{opentime,jdbcType=TIMESTAMP},
  tablecode = #{tablecode,jdbcType=CHAR},
  fooddetails = #{fooddetails,jdbcType=CHAR}
where id = #{id,jdbcType=CHAR}

mapper.select:
select id, opentime, tablecode, fooddetails
from ordertable
where id = #{id,jdbcType=CHAR}
